pragma solidity 0.8.1;

contract PremiumDog is ERC1155, Ownable, AccessControl, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenCounter;

    event NewDogMinted(address receiver, uint256 tokenId, uint8 dogType);

    mapping(uint256 => uint8) public tokenDogType;
    mapping(address => uint8) public numberOfMint;

    struct Dog {
        uint256 price;
        uint8 dogType;
        uint8 count;
    }

    Dog labrador;
    Dog akita;
    Dog hybrid;
    Dog shiba;

    uint256 private mintAmount = 1;
    bytes private mintData = "";

    string public constant name = "The Dog of AndyBimBam";
    string public constant symbol = "DABB";

    bool public saleOpen = false;

    uint256 public constant maxSupply = 10000;

    constructor() ERC1155("") {
        labrador = Dog(10000000000000000, 0, 0);
        akita = Dog(20000000000000000, 1, 0);
        hybrid = Dog(30000000000000000, 2, 0);
        shiba = Dog(40000000000000000, 3, 0);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function getDogByDogType(uint8 dogType)
        public
        view
        returns (Dog memory dog)
    {
        require(dogType < 4, "Invalid package type");

        if (dogType == 0) {
            return labrador;
        }
        if (dogType == 1) {
            return akita;
        }
        if (dogType == 2) {
            return hybrid;
        }
        if (dogType == 3) {
            return shiba;
        }
    }

    function totalSupply() public pure returns (uint256) {
        return maxSupply;
    }

    function getDogPrice(uint8 dogType) public view returns (uint256) {
        Dog memory dog = getDogByDogType(dogType);
        return dog.price;
    }

    function getDogCount(uint8 dogType) public view returns (uint256) {
        Dog memory dog = getDogByDogType(dogType);
        return dog.count;
    }

    function getCounter() public view returns (uint256) {
        return _tokenCounter.current();
    }

    function createPack(uint8 dogType) public payable nonReentrant {
        Dog memory dog = getDogByDogType(dogType);

        require(_tokenCounter.current() < maxSupply, "Max supply reached!");

        require(numberOfMint[msg.sender] < 4, "Address has already minted!");

        require(saleOpen == true, "Sale is not open yet!");

        require(msg.value >= dog.price, "Matic value sent is not correct");

        uint256 newTokenId = _tokenCounter.current();

        tokenDogType[newTokenId] = dog.dogType;

        numberOfMint[msg.sender]++;

        dog.count++;

        _tokenCounter.increment();

        _mint(msg.sender, newTokenId, mintAmount, mintData);

        emit NewDogMinted(msg.sender, newTokenId, dogType);
    }

    function uri(uint256 tokenId)
        public
        view
        override
        returns (string memory myjson)
    {
        require(tokenDogType[tokenId] <= 3, "Invalid package type");

        if (tokenDogType[tokenId] == 0) {
            return (
                string(
                    abi.encodePacked(
                        "https://raw.githubusercontent.com/AndyWaiHim/MyMetadata/main/dog/labradro.json"
                    )
                )
            );
        }
        if (tokenDogType[tokenId] == 1) {
            return (
                string(
                    abi.encodePacked(
                        "https://raw.githubusercontent.com/AndyWaiHim/MyMetadata/main/dog/akita.json"
                    )
                )
            );
        }
        if (tokenDogType[tokenId] == 2) {
            return (
                string(
                    abi.encodePacked(
                        "https://raw.githubusercontent.com/AndyWaiHim/MyMetadata/main/dog/hybrid.json"
                    )
                )
            );
        }
        if (tokenDogType[tokenId] == 3) {
            return (
                string(
                    abi.encodePacked(
                        "https://raw.githubusercontent.com/AndyWaiHim/MyMetadata/main/dog/shiba.json"
                    )
                )
            );
        }
    }

    function changePrice(uint256[] memory dogType, uint256[] memory newPrice)
        public
        onlyOwner
    {
        require(saleOpen == false, "sale is not open now!");
        require(
            dogType.length == newPrice.length,
            "newPrice length must be equal to packType length"
        );
        for (uint256 i = 0; i < dogType.length; i++) {
            if (dogType[i] == 0) {
                labrador.price = newPrice[i];
            }
            if (dogType[i] == 1) {
                akita.price = newPrice[i];
            }
            if (dogType[i] == 2) {
                hybrid.price = newPrice[i];
            }
            if (dogType[i] == 3) {
                shiba.price = newPrice[i];
            }
        }
    }

    function changeSaleOpen(bool toggleSale) public onlyOwner {
        saleOpen = toggleSale;
    }

    function burn(uint256 id, uint256 amount) external {
        _burn(msg.sender, id, amount);
    }
}

